interface Props<T> {
    name: string;
    label?: string;
    multiline?: T;
}
declare type InputProps = JSX.IntrinsicElements['input'] & Props<false>;
declare type TextAreaProps = JSX.IntrinsicElements['textarea'] & Props<true>;
export default function Input({ name, label, multiline, ...rest }: InputProps | TextAreaProps): JSX.Element;
export {};
